mapper.update:
UPDATE meeting

 meeting_views = NVL(meeting_views,0) +1

WHERE meeting_no = #{meetingNo}

UPDATE meeting
meeting_center =#{meetingCenter:VARCHAR}, meeting_name =#{meetingName:VARCHAR}, title_img =#{titleImg:VARCHAR}, meeting_detail =#{meetingDetail:VARCHAR}, meeting_rule =#{meetingRule:VARCHAR}, interest_name =#{interestName:VARCHAR}, meeting_location =#{meetingLocation:VARCHAR}, meeting_date =#{meetingDate:DATE}, meeting_day =#{meetingDay:VARCHAR}, meeting_time =#{meetingTime:VARCHAR}, snooze =#{snooze:CHAR}, meeting_dues =#{meetingDues:VARCHAR}, meeting_crew_limit =#{meetingCrewLimit:NUMERIC}
WHERE meeting_no = #{meetingNo}
