mapper.update:
UPDATE users

	last_login_day = #{lastLoginDay},
	attend_count = #{attendCount}

WHERE user_id = #{userId}

UPDATE users
nick_name = #{nickName}, address = #{address}, phone = #{phone}, email = #{email}, first_interest = #{firstInterest}, second_interest = #{secondInterest}, third_interest = #{thirdInterest}, profile_img = #{profile}
WHERE user_id = #{userId}

UPDATE users

	my_type 	= #{myType},
	first_type  = #{firstType},
	second_type = #{secondType},
	thrid_type  = #{thirdType}

WHERE user_id = #{userId}

mapper.select:
SELECT sender_id, receiver_id, send_date, receive_date, letter_title, letter_detail, letter_no
FROM letter

letter_no = #{no}

